svg.script:
const langs = [
  {name:"HTML",  val:38, color:"#e34c26"},
  {name:"Ren'Py",val:25, color:"#ff6fb7"},
  {name:"JS",    val:18, color:"#f1e05a"},
  {name:"Python",val:8,  color:"#3572A5"},
  {name:"Java",  val:6,  color:"#b07219"},
  {name:"CSS",   val:5,  color:"#563d7c"}
];

let x=20, y=40, scale=3.2, legendY=70;

langs.forEach((l,i)=>{
  const w=l.val*scale;
  document.write(`<rect x="${x}" y="${y}" width="${w}" height="8" fill="${l.color}" class="bar"/>`);
  document.write(`<circle cx="25" cy="${legendY+i*15}" r="4" fill="${l.color}"/>`);
  document.write(`<text x="35" y="${legendY+4+i*15}" class="lang">${l.name} <tspan class="percent">${l.val}%</tspan></text>`);
  x+=w;
});
